      imgs,
      garare, urls, files } = this.state;
    let images = [];
    let promises = [];
    if (this.validateForm()) {
      Object.values(files).forEach((img, index) => {
        const name = Date.now();
        let imagePath = `images/house/${index + name}`;
        const uploadTask = this.props.firebase.storage
          .ref(imagePath)
          .put(img)
          .then();
        promises.push(uploadTask);
      });
      Promise.all(promises).then(snapshots => {
        let urlsPromises = [];
        snapshots.forEach(snapshot => {
          urlsPromises.push(snapshot.ref.getDownloadURL());
        });
        Promise.all(urlsPromises).then(urlSnapshots => {
          this.props.firebase
            .houses()
            .push({
              // ...newHouse,
              images: urlSnapshots,
              propertyInfor: {
                description,
                details:{
                  address,
                  area,
                  schoolDistrict,
                  lotSize,
                  squareFeet,
                  listPrice,
                  year,
                  beds,
                  baths,
                  cool,
                  heat,
                  firePlace,
                  garare
                }
              },
              tour,
            })
            .then(() => {
              this.setState({ ...INITIAL_STATE });
            })
            .catch(error => {
              this.setState({ error });
            });
        });
      });
    }

    event.preventDefault();
  };

