class AddHouse extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
     ...INITIAL_STATE,
     propertyInfor: {},
     details:{},
     thumbnail: "",
     avatarFile: "",
     isUploading: false,
     errors: {},
     progress: 0
   };
