import React, { Component } from "react";

/* Import Components */
import Input from "./formcomponents/Input";

import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import ButtonToolbar from "react-bootstrap/ButtonToolbar";
import Form from "react-bootstrap/Form";

// redirect
import { Redirect, Router, Route } from "react-router-dom";
import * as ROUTES from "../../constants/routes";
import { withRouter } from "react-router";

//for placeholder name and email
import { withFirebase } from "../../server/Firebase/index";
import "./client.css";
//import footer

let path;
let remember;
var info = [];

const authUser = JSON.parse(localStorage.getItem("authUser"));

class SubmitForm extends React.Component {
  constructor(props) {
    super(props);
    path = this.props.history;
    // console.log(path.location.state[0]);

    this.state = {
      isLoading: false,
      newUser: {
        avatar: authUser.avatar,
        email: authUser.email,
        favHouses: authUser.favHouses,
        firstName: authUser.firstName,
        isClient: authUser.isClient,
        lastName: authUser.lastName,
        passwordOne: authUser.passwordOne,
        phonenumber: authUser,phonenumber,
    }
  };

this.handleFirstName = this.handleFirstName.bind(this);
this.handleLastName = this.handleLastName.bind(this);
this.handleEmail = this.handleEmail.bind(this);
this.handleClearForm = this.handleClearForm.bind(this);
this.handleInput = this.handleInput.bind(this);
 this.handleFormSubmit = this.handleFormSubmit.bind(this);
  }

  handleFirstName(e) {
      let value = e.target.value;
      this.setState( prevState => ({ newUser :
           {...prevState.newUser, firstName: value
           }
     }))
     }

     handleLastName(e) {
      let value = e.target.value;
      this.setState( prevState => ({ newUser :
           {...prevState.newUser, lastName: value
           }
         }))
     }

  handleEmail(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          email: value
        }
      }),
      () => console.log(this.state.newUser)
    );
  }

  handleInput(e) {
    let value = e.target.value;
    let name = e.target.name;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          [name]: value
        }
      }),
      () => console.log(this.state.newUser)
    );
  }

  handlePassword(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          password: value
        }
      }),
      () => console.log(this.state.newUser)
    );
  }


  handleClearForm(e) {
    e.preventDefault();
    this.setState({
      newUser: {
        avatar: authUser.avatar,
        email: authUser.email,
        favHouses: authUser.favHouses,
        firstName: authUser.firstName,
        isClient: authUser.isClient,
        lastName: authUser.lastName,
        passwordOne: authUser.passwordOne,
        phonenumber: authUser,phonenumber,
      }
    });
  }

  handleFormSubmit(e) {
    e.preventDefault();
    let userData = this.state.newUser;
    this.props.firebase.doUpdateProfile(userData);
  }

  componentDidMount() {
      let newState = [];
      newState.push({
        firstName: authUser.firstName,
        lastName: authUser.lastName,
        email: authUser.email,
                });
      this.setState({newUser: newState});
    }

  render() {
    remember = this.state.dis;
    return (
      <div className= "bg">
      <div className="ClientEditProfile">
        <Form onSubmit={this.handleFormSubmit}>
          <h3 className="HeaderEdit"> Edit your account information</h3>
          <div className="EditFormClient">
            <span className="PreEditName">First Name: </span>
            <br />
            <input
              name="firstName"
              type="text"
              className="Clientform-control"
              value={this.state.newUser[0]?this.state.newUser[0].firstName:null}
              onChange={this.handleInput}
            />

            <span className="PreEditName">Last Name: </span>
            <br />
            <input
              name="lastName"
              type="text"
              className="Clientform-control"
              value={this.state.newUser[0]?this.state.newUser[0].lastName:null}
              onChange={this.handleInput}
            />

            <span className="PreEditName">Your Email: </span>
            <br />
            <input
              name="email"
              type="email"
              className="Clientform-control"
              placeholder={"Enter your new password"}
              value={this.state.newUser[0]?this.state.newUser[0].email:null}
              onChange={this.handleInput}
            />

            <div className="ButtonEditClient">
              <ButtonToolbar className="justify-content-center">
              <Button
                variant="primary"
                onClick={this.handleFormSubmit}
                action={this.handleFormSubmit}
              >
                Save Changes
              </Button>
                <Button
                  variant="secondary"
                  action={this.handleClearForm}
                  onClick={this.handleClearForm}
                >
                  Edit
                </Button>
              </ButtonToolbar>
            </div>
          </div>
        </Form>
      </div>
      </div>
    );
  }
}

const buttonStyle = {
  margin: "10px 10px 10px 10px"
};

export default withFirebase(SubmitForm);
