// Add a new house
import React, { Component } from "react";
import { withFirebase } from "../../server/Firebase/index";
import FileUploader from "react-firebase-file-uploader";
import Modal from "react-responsive-modal";
import { Redirect, Router, Route } from "react-router-dom";
import * as ROUTES from "../../constants/routes";
import { withRouter } from "react-router";
import Row from "react-bootstrap/Row";
import Table from "react-bootstrap/Table";
import Col from "react-bootstrap/Col";
import { Card, Icon, Image } from "semantic-ui-react";
import Button from "react-bootstrap/Button";
import { Container } from "react-bootstrap";
import "./style.css";
import FIleUploaderShowImage from "./FIleUploaderShowImage";
let path;
let remember;

const INITIAL_STATE = {
  newHouse: {
    propertyInfor: {},
    details:{},
    address: "",
    area: "",
    schoolDistrict: "",
    lotSize: "",
    squareFeet: "",
    listPrice: "",
    year: "",
    description: "",
    tour: "",
    beds: null,
    baths: null,
    cool: null,
  heat: null,
    firePlace: null,
    imgs: [],
    garare: null
  },
  error: null,
  progress: 0,
  isUploading: false,
  files: [],
  urls: []
};

class AddHouse extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      ...INITIAL_STATE,
      propertyInfor: {},
      details:{},
    };
  }

  onSubmit = event => {
    const { newHouse, imgs, urls, files } = this.state;
    let images = [];
    let promises = [];
    if (this.validateForm()) {
      Object.values(files).forEach((img, index) => {
        const name = Date.now();
        let imagePath = `images/house/${index + name}`;
        const uploadTask = this.props.firebase.storage
          .ref(imagePath)
          .put(img)
          .then();
        promises.push(uploadTask);
      });
      Promise.all(promises).then(snapshots => {
        let urlsPromises = [];
        snapshots.forEach(snapshot => {
          urlsPromises.push(snapshot.ref.getDownloadURL());
