let path;
let remember;
let imagesource;
const authUser = JSON.parse(localStorage.getItem("authUser"));
class Client extends React.Component {
  constructor(props) {
    super(props);
    path = this.props.history;

    console.log(authUser.favHouses.hs);

    // lastInfor = this.props.history.location.state;
    this.state = {
      isLoading: false,
      dis: [],
      authUser: {},
      favoritehouses: [
      {
        img: "https://i.imgur.com/GqgkPSx.jpg",
        address: "1234 Tully Road San Jose CA,",
        price: "$300,000"
      },
      {
        img: "https://i.imgur.com/R95ADE9.jpg",
        address: "2138 ShadowBone San Jose",
        price: "$1,300,000"
      },
      {
        img: "https://i.imgur.com/uhBRgH9.jpg",
        address: "9216 Aborn San Jose",
        price: "$1,500,000"
      },
      {
        img: "https://i.imgur.com/0GvHsB5.jpg",
        address: "3637 Snell Avenue SPC364",
        price: "$300,000"
      }
      ]
    };
  }

  gettingHouse(){
    console.log(authUser.favHouses);
  }

 deleteAction() {

 }


  componentDidMount() {
    this.setState({isLoading: true});
    const authUser = JSON.parse(localStorage.getItem("authUser"));
    this.props.firebase.database.ref('account').on('value', (snapshot) => {
      const data = snapshot.val().userAccount;
      let newState = [];
      let imgs = data.userAccount1;
      console.log(imgs.passWord);
      newState.push({
                name: imgs.name,
                email: imgs.email,
                img: imgs.img,
                isAgent: imgs.isAgent,
                passWord: imgs.passWord
                });
      this.setState({dis: newState , authUser: authUser});
    });
  }

  onProfile(){
    path.push({
    pathname: './clientform'},remember);
  };

  onEdit(){
    path.push({
      pathname: './clientedit', remember
    })
  }

  onSubmit(){
    path.push({
      pathname: './overview', remember
    })
  }


  buildHouse(house){
    return(
      <div className="single-house">
        <div className="SingleHouseInfo">
          <Image src={house.img} alt="" />
          <h2> Price: {house.price}
          <br />
          {house.address}
          </h2>
        </div>
        <div style={this.state.hide ? {visibility: "hidden"}: {visibility: "visible"} }
        className="clientbutton">
        <Button variant="danger">Delete</Button>
      </div>
    </div>
    );
  }


buildFavoriteHouseList(){
  const Panel = this.state.favoritehouses.map(house=>{
    return this.buildHouse(house)
  });
  return <div>{Panel}</div>;
}

  render() {
    const {isLoading, authUser} = this.state;
    remember = this.state.dis;

    return (
      <div className="clientContent">
      {/* {isLoading ? (
        <h1> Loading...</h1>)
          :
      ( */}
        <div>
        <div className="TopBar container navRender">
          <Nav className="justify-content-center" activeKey="/home">
            <Nav.Item>
              <Nav.Link>
                <span id="nav01" className="textNav activeNav">
                  {"Favorite House"}
                </span>
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link href={ROUTES.HOMEPAGE}>
                <span id="nav03" className="textNav">
                  {"Find a house"}
                </span>
              </Nav.Link>
            </Nav.Item>
          </Nav>
        </div>

        <div className="row">
        <div className="ClientColumn col-sm-2">
        <Card style={{ "box-shadow": "5px 4px 8px 5px rgba(0,0,0,0.2)" }} className="ClientInfo">
        <Card.Img className="ClientImage" variant="top" src={"https://i.imgur.com/onLuiBV.jpg"}/>
        <Card.Body>
        <Card.Title>
          <h3 class="ClientName">{authUser.firstName + " " +authUser.lastName}</h3>
        </Card.Title>
        {authUser.isAgent !== "true" ? (
                      <div
                        className="btn-edit"
                        style={
                          this.state.hide
                            ? { visibility: "hidden" }
                            : { visibility: "visible" }
                        }
                      >
                        <Button onClick={e => this.onEdit()}>
                          Edit Profile
                        </Button>
                      </div>
                    ) : (
                      ""
                    )}
        </Card.Body>
        </Card>
        {/* <p>Hello </p> */}
        </div>
        <div className="FavoriteHouseColumn col-sm-9">
        {this.buildFavoriteHouseList()}
        {/* <p>Hello</p> */}
        </div>
        </div>
        </div>


      {/* )
      } */}
      </div>
    );
  }




}

const imageTag={
  render(){
    return(
      <p>me</p>
    )
  }

};

const styles = {
  card: {
    maxWidth: 345,
  },
  media: {
    objectFit: 'cover',
  },
};

// createCard.propTypes = {
