// Add a new house
import React, { Component } from "react";
import { withFirebase } from "../../server/Firebase/index";
import FileUploader from "react-firebase-file-uploader";
import Modal from "react-responsive-modal";
import firebase from "firebase";
import { Redirect, Router, Route } from "react-router-dom";
import * as ROUTES from "../../constants/routes";
import { withRouter } from "react-router";
import Row from "react-bootstrap/Row";
import Table from "react-bootstrap/Table";
import Col from "react-bootstrap/Col";
import { Card, Icon, Image } from "semantic-ui-react";
import Button from "react-bootstrap/Button";
import { Container } from "react-bootstrap";
import "./style.css";
import FIleUploaderShowImage from "./FIleUploaderShowImage";
import { Snackbar } from "material-ui";
let path;
let remember;

const authUser = JSON.parse(localStorage.getItem("authUser"));
console.log(authUser);

const INITIAL_STATE = {

  agent: {
    avatar: authUser.avatar,
    email: authUser.email,
    firstName: authUser.firstName,
    isAgent: true,
    lastName: authUser.lastName,
    licenseNumber: authUser.licenseNumber,
    officeLocation: authUser.officeLocation,
    phonenumber: authUser.phonenumber,
    // aboutme: authUser.aboutme
  },
  propertyInfor: {
    description: "",
    details: {
      address: "",
      area: "",
      lotSize: "",
      squareFeet: "",
      listPrice: "",
      year: "",
      beds: null,
      baths: null,
      cool: null,
      firePlace: null,
      // imgs: [],
      garage: null,
    }
  },
  render: [],
  tour: "",
  error: null,
  thumbnail: "",
  progress: 0,
  isUploading: false,
  files: [],
  urls: [],
  fileNames: []
};

class AddHouse extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      ...INITIAL_STATE,
      propertyInfor: {
        description: "",
        details: {
          address: "",
          area: "",
          lotSize: "",
          squareFeet: "",
          listPrice: "",
          year: "",
          beds: null,
          baths: null,
          cool: null,
          firePlace: null,
          // imgs: [],
          garage: null,
        }
      },
      thumbnail: "",
      avatarFile: "",
      isUploading: false,
      errors: {},
      progress: 0
    };

  }


  handlePrice = event => {
    console.log(INITIAL_STATE.propertyInfor.details);
   this.setState({
    listPrice: event.target.value
   });
  }

  onSubmit = event => {
    const {
      newHouse,
      files,
      fileNames,
      thumbnail,
      agent: {
        avatar,
        email,
        firstName,
        isAgent,
        lastName,
        licenseNumber,
        officeLocation,
        phonenumber,
        // aboutme
      },
      propertyInfor: {
        description,
        details: {
          address,
          area,
          lotSize,
          squareFeet,
          listPrice,
          year,
          beds,
          baths,
          cool,
          firePlace,
          // imgs: [],
          garage,
        }
      },
      render,
      tour
    } = this.state;

    let images = [];
    let promises = [];

    if (this.validateForm()) {
      Object.values(files).forEach((img, index) => {
        const name = Date.now();
        let imagePath = `images/house/${index + name}`;
        const uploadTask = this.props.firebase.storage
          .ref(imagePath)
          .put(img)
          .then();
        promises.push(uploadTask);
      });
      Promise.all(promises).then(snapshots => {
        let urlsPromises = [];
        snapshots.forEach(snapshot => {
          urlsPromises.push(snapshot.ref.getDownloadURL());
        });
        Promise.all(urlsPromises).then(urlSnapshots => {
          this.props.firebase
            .houses()
            .push({
              // ...newHouse,
              thumbnail: thumbnail,
              images: urlSnapshots,
              agent: {
                avatar,
                email,
                firstName,
                isAgent,
                lastName,
                licenseNumber,
                officeLocation,
                phonenumber,
                // aboutme
              },
              propertyInfor: {
                description,
                details: {
                  address,
                  area,
                  lotSize,
                  squareFeet,
                  listPrice,
                  year,
                  beds,
                  baths,
                  cool,
                  firePlace,
                  // imgs: [],
                  garage,
                }
              },
              render,
              tour
            })
            .then(() => {
              this.setState({ ...INITIAL_STATE });
            })
            .catch(error => {
              this.setState({ error });
            });
        });
      });

    }

    event.preventDefault();
  };

  onChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };


  onClick = () => {
    this.props.history.push(ROUTES.LANDING);
  };

  customOnChangeHandler = event => {
    event.preventDefault();
    this.setState({
      imgs: event.target.files
    });
  };

  // Upload Thumnails
  handleUploadStart = () => this.setState({ isUploading: true, progress: 0 });
  handleProgress = progress => this.setState({ progress });
  handleUploadError = error => {
    this.setState({ isUploading: false });
    console.error(error);
  };
  handleUploadSuccess = filename => {
    this.setState({
      avatarFile: filename,
      progress: 100,
      isUploading: false
    });
    firebase
      .storage()
      .ref("images/thumbnail")
      .child(filename)
      .getDownloadURL()
      .then(url => this.setState({ thumbnail: url }));
  };

  validateForm() {
    let errors = {};
    let isValidForm = true;

    if (!this.state.address) {
      isValidForm = false;
      errors["address"] = "Please Enter An Address House";
    }
    if (!this.state.zipcode) {
      isValidForm = false;
      errors["zipcode"] = "Please Enter An ZipCode";
    }
    if (!this.state.area) {
      isValidForm = false;
      errors["area"] = "Please Enter An Area";
    }
    if (!this.state.city) {
      isValidForm = false;
      errors["city"] = "Please Enter An City";
    }
    if (!this.state.usstate) {
      isValidForm = false;
      errors["usstate"] = "Please Enter An US State";
    }
    if (!this.state.lotsize) {
      isValidForm = false;
      errors["lotsize"] = "Please Enter Lot Size of This House";
    }
    if (!this.state.squarefeet) {
      isValidForm = false;
      errors["squarefeet"] = "Please Enter Square Feet House";
    }
    if (!this.state.squarefeet) {
      isValidForm = false;
      errors["squarefeet"] = "Please Enter Square Feet House";
    }
    if (!this.state.price) {
      isValidForm = false;
      errors["price"] = "Please Enter A Price";
    }
    if (!this.state.year) {
      isValidForm = false;
      errors["year"] = "Please Enter A Year";
    }

    this.setState({
      errors: errors
    });
    return isValidForm;
  }
  render() {
    return (
      <div
        className="AddNewHouse"
        style={{
          border: "1px solid black",
          margin: "100px",
          borderRadius: "10px",
          boxShadow: "2px 2px 2px 1px rgba(0, 140, 186, 0.5)"
        }}
      >
        <Container style={{ margin: "auto" }}>
          <div class="row">
            <div
              class="col-md-9 personal-info"
              style={{
                margin: "auto",
                float: "left"
              }}
            >
              <h2 style={{ textAlign: "center", marginTop: "15px" }}>
                Add A New House
              </h2>

              <form
                class="form-horizontal"
                role="form"
                onSubmit={this.onSubmit}
              >
                <div class="form-group">
                  <label class="col-lg-3 control-label">Address:</label>
                  <div class="col-lg-8">
                    <input
                      class="form-control"
                      name="address"
                      type="text"
                      placeholder={"Enter Address House"}
                      onChange={this.onChange}
                      value={this.state.address}
                      style={{ marginBottom: "2px" }}
                    />

                    <span id="errorMsgAddhouse130">
                      {this.state.errors.address}
                    </span>
                  </div>
                </div>

                <div class="form-group">
                  <label class="col-lg-3 control-label">
                    Zipcode and Area:
                  </label>
                  <div class="col-lg-4">
                    <input
                      class="form-control"
                      type="text"
                      name="zipcode"
                      placeholder={"Enter Zipcode"}
                      onChange={this.onChange}
                      value={this.state.zipcode}
                      style={{ marginBottom: "2px" }}
                    />

                    <span id="errorMsgAddhouse130">
                      {this.state.errors.zipcode}
                    </span>
                  </div>
                  <div class="col-lg-4">
                    <input
                      class="form-control"
                      type="text"
                      name="area"
                      placeholder={"Enter Area"}
                      onChange={this.onChange}
                      value={this.state.area}
                      style={{ marginBottom: "2px" }}
                    />

                    <span id="errorMsgAddhouse130">
                      {this.state.errors.area}
                    </span>
                  </div>
                </div>

                <div class="form-group">
                  <label class="col-lg-3 control-label">City and State:</label>
                  <div class="col-lg-4">
                    <input
                      class="form-control"
                      type="text"
                      name="city"
                      placeholder={"Enter City"}
                      onChange={this.onChange}
                      value={this.state.city}
                      style={{ marginBottom: "2px" }}
                    />

                    <span id="errorMsgAddhouse130">
                      {this.state.errors.city}
                    </span>
                  </div>
                  <div class="col-lg-4">
                    <input
                      class="form-control"
                      type="text"
                      name="usstate"
                      placeholder={"Enter State"}
                      onChange={this.onChange}
                      value={this.state.usstate}
                      style={{ marginBottom: "2px" }}
                    />

                    <span id="errorMsgAddhouse130">
                      {this.state.errors.usstate}
                    </span>
                  </div>
                </div>

                {/* lot size and square feet */}
                <div class="form-group">
                  <label class="col-lg-3 control-label">
                    Lot Size and Square Feet
                  </label>
                  <div class="col-lg-4">
                    <input
                      class="form-control"
                      type="text"
                      name="lotsize"
                      placeholder={"Enter Lotsize"}
                      onChange={this.onChange}
                      value={this.state.lotsize}
                      style={{ marginBottom: "2px" }}
                    />

                    <span id="errorMsgAddhouse130">
                      {this.state.errors.lotsize}
                    </span>
                  </div>
                  <div class="col-lg-4">
                    <input
                      class="form-control"
                      type="text"
                      name="squarefeet"
                      placeholder={"Enter Square Feet House"}
                      onChange={this.onChange}
                      value={this.state.squarefeet}
                      style={{ marginBottom: "2px" }}
                    />

                    <span id="errorMsgAddhouse130">
                      {this.state.errors.squarefeet}
                    </span>
                  </div>
                </div>

                {/* List price and year */}
                <div class="form-group">
                  <label class="col-lg-3 control-label">
                    List price and year
                  </label>
                  <div class="col-lg-4">
                    <input
                      class="form-control"
                      type="text"
                      name="listPrice"
                      placeholder={"Enter Your Listing Price"}
                      onChange={this.handlePrice}
                      value={this.state.price}
                      style={{ marginBottom: "2px" }}
                    />

                    <span id="errorMsgAddhouse130">
                      {this.state.errors.price}
                    </span>
                  </div>
                  <div class="col-lg-4">
                    <input
                      class="form-control"
                      type="text"
                      name="year"
                      placeholder={"Enter Your House Year"}
                      onChange={this.onChange}
                      value={this.state.year}
                      style={{ marginBottom: "2px" }}
                    />

                    <span id="errorMsgAddhouse130">
                      {this.state.errors.year}
                    </span>
                  </div>
                </div>

                <div class="form-group">
                  <label class="col-lg-3 control-label">Description:</label>
                  <div class="col-lg-8">
                    <textarea
                      rows={4}
                      cols={50}
                      name="description"
                      form="usrform"
                      defaultValue={"Enter text here..."}
                      onChange={this.onChange}
                      value={this.state.description}
                      style={{ marginBottom: "2px" }}
                    />
                  </div>
                </div>
                {/* 360 degree tour */}
                <div class="form-group">
                  <label class="col-md-3 control-label">360-degree Tour:</label>
                  <div class="col-md-8">
                    <input
                      class="form-control"
                      type="text"
                      name="tour360"
                      placeholder={"Enter Your 360 Tour"}
                      onChange={this.onChange}
                      value={this.state.tour360}
                      style={{ marginBottom: "2px" }}
                    />
                  </div>
                </div>

                {/* Add images */}
                <div class="form-group">
                  <label class="col-md-3 control-label">
                    Add your house images
                  </label>
                  <div className="col-md-8">
                    {/* <dihhv className="container"> */}
                    <FIleUploaderShowImage
                      setIsUploading={isUploading =>
                        this.setState({ isUploading: isUploading })
                      }
                      setProgress={progress =>
                        this.setState({ progress: progress })
                      }
                      setFiles={files => this.setState({ files: files })}
                      // ⇐ reference the component
                    />
                    <div id="HouseImagesList">
                      {this.state.files.map(file => {
                        const result = URL.createObjectURL(file);

                        return <img id="test12389" alt="132" src={result} />;
                      })}
                    </div>
                    {/* </dihhv> */}
                  </div>
                </div>

                <div class="form-group">
                  <label class="col-md-3 control-label">
                    Add your house thumbnail
                  </label>
                  <div class="col-md-8">
                    <label>
                      <Image
                        rounded
                        name="thumbnail"
                        src={
                          this.state.progress !== 100
                            ? "https://firebasestorage.googleapis.com/v0/b/senior-project-se-195.appspot.com/o/images%2Ficons%2FhouseIcon.png?alt=media&token=2be3cbe3-961c-42b1-a84f-4f7ef19dba1b"
                            : this.state.thumbnail
                        }
                        onChange={this.onChange}
                        style={{
                          border: "2px solid grey",
                          borderRadius: "8px",
                          padding: "4px",
                          witdh: "80px",
                          height: "80px",
                          textAlign: "left",
                          display: "flex"
                        }}
                      />
                      <FileUploader
                        hidden
                        // randomizeFilename
                        accept="image/*"
                        storageRef={this.props.firebase.storageRef3}
                        onUploadStart={this.handleUploadStart}
                        onUploadError={this.handleUploadError}
                        onUploadSuccess={this.handleUploadSuccess}
                        onProgress={this.handleProgress}
                      />
                    </label>
                  </div>
                </div>

                {/* Bad, bed and other useful info */}
                <div class="form-group">
                  <form action="/action_page.php">
                    <label class="col-md-3 control-label">
                      <img src="https://img.icons8.com/ios-glyphs/30/000000/bedroom.png" />
                    </label>
                    <div class="col-md-2">
                      <input
                        class="form-control"
                        type="text"
                        name="bedroom"
                        onChange={this.onChange}
                        value={this.state.bedroom}
                      />
                    </div>
                    <label class="col-md-3 control-label">
                      <img src="https://img.icons8.com/ios-glyphs/30/000000/shower.png" />
                    </label>
                    <div class="col-md-2">
                      <input
                        class="form-control"
                        type="text"
                        name="bathroom"
                        onChange={this.onChange}
                        value={this.state.bathroom}
                      />
                    </div>
                  </form>
                </div>
                {/* Cooler and Heater */}
                <div class="form-group">
                  <form action="/action_page.php">
                    <label class="col-md-3 control-label">
                      <img src="https://img.icons8.com/ios-glyphs/30/000000/air-conditioner.png" />
                    </label>
                    <div class="col-md-2">
                      <input
                        class="form-control"
                        type="text"
                        name="cooler"
                        onChange={this.onChange}
                        value={this.state.cooler}
                      />
                    </div>
                    <label class="col-md-3 control-label">
